mapper.select:
SELECT * FROM appinfodb.app_info tb, (SELECT id AS cId1, categoryName AS categoryName1 FROM appinfodb.app_category WHERE parentId IS NULL) tb1, (SELECT id AS cId2, categoryName AS categoryName2 FROM appinfodb.app_category WHERE parentId IS NOT NULL AND parentId IN (SELECT id FROM appinfodb.app_category WHERE parentId IS NULL)) tb2, (SELECT id AS cId3, categoryName AS categoryName3 FROM appinfodb.app_category WHERE parentId IS NOT NULL AND parentId NOT IN (SELECT id FROM appinfodb.app_category WHERE parentId IS NULL) AND parentId IN (SELECT id FROM appinfodb.app_category WHERE parentId IS NOT NULL AND parentId IN (SELECT id FROM appinfodb.app_category WHERE parentId IS NULL))) tb3, (SELECT valueId fId, valueName flatFormName FROM appinfodb.data_dictionary WHERE typeCode = 'APP_FLATFORM') tb4, (SELECT valueId sId, valueName statusName FROM appinfodb.data_dictionary WHERE typeCode = 'APP_STATUS') tb5, (SELECT info.id AS appId, version.versionNo FROM app_info info LEFT JOIN app_version version ON info.versionId = version.id) tb6, (SELECT id devId1, devCode, devName FROM appinfodb.dev_user) tb7 WHERE tb.categoryLevel1 = tb1.cId1 AND tb.categoryLevel2 = tb2.cId2 AND tb.categoryLevel3 = tb3.cId3 AND tb.flatformId = tb4.fId AND tb.`status` = tb5.sId AND tb.id = tb6.appId AND tb.devId = tb7.devId1 and tb.devId=#{appInfo.devId}
and tb.softwareName like concat('%',#{appInfo.softwareName},'%')
and tb.status = #{appInfo.status}
and tb.flatformId =#{appInfo.flatformId}
and tb.categoryLevel1=#{appInfo.categoryLevel1}
and tb.categoryLevel2=#{appInfo.categoryLevel2}
and tb.categoryLevel3=#{appInfo.categoryLevel3}
and tb.devId=#{appInfo.devId}
limit #{pageIndex},#{pageSize};

select count(1) from app_info


        and softwareName like concat('%',#{softwareName},'%')


        and status = #{status}


        and flatformId =#{flatformId}


        and categoryLevel1=#{categoryLevel1}


        and categoryLevel2=#{categoryLevel2}


        and categoryLevel3=#{categoryLevel3}


        and devId=#{devId}
